function Carousel() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const images = ['image1.jpg', 'image2.jpg', 'image3.jpg'];

  const nextImage = () => {
    setCurrentIndex((prev) => (prev + 1) % images.length);
  };

  const prevImage = () => {
    setCurrentIndex((prev) => (prev - 1 + images.length) % images.length);
  };

  return (
    <div className="relative w-full">
      <img src={images[currentIndex]} className="w-full h-64 object-cover rounded-lg" alt="Carousel" />
      <button onClick={prevImage} className="absolute left-2 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full">&lt;</button>
      <button onClick={nextImage} className="absolute right-2 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full">&gt;</button>
    </div>
  );
}

export default Carousel;

