import React, { useState, useEffect } from 'react';

function Scrollspy() {
  const [activeSection, setActiveSection] = useState(1);

  useEffect(() => {
    const handleScroll = () => {
      const sections = document.querySelectorAll('section');
      let currentIndex = 0;
      sections.forEach((section, index) => {
        const rect = section.getBoundingClientRect();
        if (rect.top <= 0 && rect.bottom > 0) currentIndex = index + 1;
      });
      setActiveSection(currentIndex);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div>
      <div className="fixed top-0 right-0 bg-white p-4">
        Active Section: {activeSection}
      </div>
      <section className="h-screen bg-gray-100 p-8" id="section1">Section 1</section>
      <section className="h-screen bg-gray-200 p-8" id="section2">Section 2</section>
      <section className="h-screen bg-gray-300 p-8" id="section3">Section 3</section>
    </div>
  );
}

export default Scrollspy;

