import React, { useState } from 'react';

function CartPage() {
  const [cartItems, setCartItems] = useState([
    { id: 1, name: 'Product 1', price: '$20.00', quantity: 1 },
    { id: 2, name: 'Product 2', price: '$30.00', quantity: 2 },
  ]);

  const handleQuantityChange = (id, quantity) => {
    setCartItems((prevState) =>
      prevState.map((item) =>
        item.id === id ? { ...item, quantity: Math.max(1, quantity) } : item
      )
    );
  };

  const handleRemove = (id) => {
    setCartItems((prevState) => prevState.filter((item) => item.id !== id));
  };

  const totalPrice = cartItems.reduce((total, item) => total + item.price.replace('$', '') * item.quantity, 0);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold text-center mb-6">Your Cart</h1>
      <div className="space-y-4">
        {cartItems.map((item) => (
          <div key={item.id} className="flex justify-between items-center bg-white shadow-md rounded-md p-4">
            <div>
              <h3 className="text-xl font-semibold">{item.name}</h3>
              <p className="text-gray-600">{item.price}</p>
            </div>
            <div className="flex items-center">
              <button className="px-3 py-1 bg-gray-200 rounded-md" onClick={() => handleQuantityChange(item.id, item.quantity - 1)}>-</button>
              <input
                type="number"
                value={item.quantity}
                onChange={(e) => handleQuantityChange(item.id, +e.target.value)}
                className="w-12 text-center border rounded-md mx-2"
              />
              <button className="px-3 py-1 bg-gray-200 rounded-md" onClick={() => handleQuantityChange(item.id, item.quantity + 1)}>+</button>
              <button className="ml-4 text-red-500" onClick={() => handleRemove(item.id)}>&#10005;</button>
            </div>
          </div>
        ))}
      </div>
      <div className="mt-6 text-right">
        <p className="text-xl font-semibold">Total: ${totalPrice.toFixed(2)}</p>
        <button className="mt-4 bg-blue-500 text-white py-2 px-6 rounded-md">Proceed to Checkout</button>
      </div>
    </div>
  );
}

export default CartPage;

