features.example:
let age: number = 25;
function greet(name: string): string {
  return `Hello, ${name}`;
}

interface User {
  name: string;
  age: number;
  greet(): void;
}
const user: User = {
  name: 'John',
  age: 30,
  greet() {
    console.log('Hello!');
  }
};

class Person {
  private name: string;
  constructor(name: string) {
    this.name = name;
  }
  public greet() {
    console.log(`Hello, ${this.name}`);
  }
}
const john = new Person('John');
john.greet();

enum Direction {
  Up = 1,
  Down,
  Left,
  Right
}
let move: Direction = Direction.Up;
console.log(move); // 1

function identity<T>(value: T): T {
  return value;
}
const result = identity<string>('Hello');
console.log(result);

// Union
let value: string | number;
value = 'Hello';
value = 42;

// Intersection
type Admin = { admin: boolean };
type User = { name: string };
type AdminUser = Admin & User;
const adminUser: AdminUser = { admin: true, name: 'John' };

// Partial
interface Todo {
  title: string;
  completed: boolean;
}
const todo: Partial<Todo> = { title: 'Learn TypeScript' };

// Readonly
const readonlyTodo: Readonly<Todo> = { title: 'Learn TypeScript', completed: false };

function isString(value: unknown): value is string {
  return typeof value === 'string';
}
if (isString('hello')) {
  console.log('It is a string');
}

type User = {
  id: number;
  name: string;
};
type ReadonlyUser = {
  readonly [K in keyof User]: User[K];
};

function error(message: string): never {
  throw new Error(message);
}

function Log(target: any, key: string) {
  console.log(`${key} was accessed`);
}
class User {
  @Log
  name: string;
  constructor(name: string) {
    this.name = name;
  }
}

